import './Botao_esp.css';
import React, { useState } from 'react';


 const Botao_esp = props => {
  const [conclusao, setConclusao] = useState(props.conclusao || 'Conclusao');
  const [dificuldade, setDificuldade] = useState(props.dificuldade || 'Dificuldade');
  const [tempo, setTempo] = useState(props.tempo || 'Tempo');


  return (
    <div className="casa_pergunta">
      <div className="front">
        {props.children}
      </div>
      <div className="back">
        <p>{conclusao}</p>
        <p>{dificuldade}</p>
        <p>{props.children}</p>
        <p>{tempo}</p>
      </div>
    </div>
  );
}

export default Botao_esp;
