import './Botao_esp.css';
import { useState } from 'react';
import React, { Component } from 'react';

export default class Botao_esp extends Component {

  constructor(props){
    super(props);
    this.titulo = props;
    this.dificuldade = props;
    this.conclusao = props;
    this.tempo = 0;
  }

render() {  
  return (

    <div class="casa_pergunta">
      <div class="front">
        {this.props.titulo}
      </div>
      <div class="back">
        <p>{this.props.conclusao}</p>
        <p>{this.props.dificuldade}</p>
        <p>{this.props.titulo}</p>
        <p>{this.tempo}</p>
      </div>
    </div>

  );
}

}
